import CartItem from "./CartItem";

const root = document.getElementById('root');

const foodData = useContext(ProjectData);

function Cart() {

    return createPortal(
        <Card>
            {foodData.Food.map((item) => {item.amount > 0 ? 
                <CartItem 
                    name = {item.name}
                    desc = {item.desc}
                    price = {item.price}
                    amount = {item.amount}
                    key = {item.name}
                /> : null
            })}
            <h2>Total amount</h2>
            <p>{totalAmountOfFood}</p>    
        </Card>,
        root
    );
}

