import { useContext, useEffect } from "react";
import ProjectData from "../store/data";
import Button from "../UI/Button";
import "./Header.css"

let amountOfItems = 0;

function Header () {
    const dataCtx = useContext(ProjectData);
    
    useEffect(() => {
        for (const item in dataCtx.Food) {
            amountOfItems += item.amount;
        }
    }, [dataCtx.Food])

    return (
        <header>
            <h1>ReactMeals</h1>
            <Button className='cartButton'><i className="fa fa-shopping-cart" style={{'fontSize':'1.25rem'}}></i> 
            <p className="CartTXT">Your Cart</p>
            <span className="numberOfCartItems">{amountOfItems}</span>
            </Button>
        </header>
    )
}

export default Header;
