import React, {useContext, useState, useEffect} from 'react';
import './Main.css';
import RestaurantTable from '../images/RestaurantTable.PNG';
import Card from '../UI/Card';
import Hero from './Hero';
import ProjectData from '../store/data';
import FoodItem from './FoodItem';
import Cart from './CartWindow/Cart';

function Main (props) {
    const foodCtx = useContext(ProjectData);

    const [cartDisplay, setCartDisplay] = useState(false);

    useEffect(() => {
        const handlerCallback = (value) => {
            setCartDisplay(value);
        };

        handler(handlerCallback);

        return () => {
            setCartDisplay(false);
        };
    }, [handler]); 

    return (
        <main>
            <img src={RestaurantTable} className='diagonal-cut' alt='a restaurant table'/>
            <Hero />
            <Card className='foodCard'>
                {foodCtx.Food.map((item) => 
                    <FoodItem 
                    name = {item.name}
                    desc = {item.desc}
                    price = {item.price}
                    key = {item.name}
                />
    )}
            </Card>
            {(foodCtx.FoodCounter > 0 && cartDisplay) ? 
                <Cart cartDisplay = {setCartDisplay}/> : null
        }
        </main>
    )
}

export default Main;
