import React, {useState, useContext, useEffect} from "react";
import ProjectData from "./store/data";
import Main from "./components/Main";
import Header from "./components/Header";
import Cart from "./components/CartWindow/Cart";


function App() {
  const foodCtx = useContext(ProjectData);

  const [CartActive, setCartActive] = useState(false);

  useEffect(() => {
    let checker = 0;
    for (const item of foodCtx.Food) {
        if (item.amount > 0) {
          checker = 1;
          return
        }
    }
    if (checker === 0) {
      setCartActive(false);
    } 
  }, [foodCtx.Food])

  return (
    <React.Fragment>
      <Header setCartActive={setCartActive}></Header>
      <Main></Main>
      {(foodCtx.FoodCounter > 0 && CartActive) ? 
                <Cart /> : null
        }
    </React.Fragment>
  );
}

export default App;
