import React, {useContext, useState, useEffect} from 'react';
import './Main.css';
import RestaurantTable from '../images/RestaurantTable.PNG';
import Card from '../UI/Card';
import Hero from './Hero';
import ProjectData from '../store/data';
import FoodItem from './FoodItem';
import Cart from './CartWindow/Cart';

function Main ({handler}) {
    const foodCtx = useContext(ProjectData);

    const [cartDisplay, setCartDisplay] = useState(false);


    useEffect(() => {
        // Simulate an asynchronous operation, such as fetching data
        const fetchData = async () => {
          try {
            // Example: Fetch data from an API
            const data = await fetch('https://api.example.com/data');
            // Example: Parse the response data
            const parsedData = await data.json();
            // Example: Update the state after fetching data
            handler(true);
          } catch (error) {
            console.error('Error fetching data:', error);
          }
        };
    
        // Call the fetchData function
        fetchData();
    
        // Cleanup function (optional)
        return () => {
          // Cancel any pending asynchronous tasks
          // This is important to prevent memory leaks
          // Example: Cancel an ongoing fetch request
          // abortController.abort();
        };
      }, [handler]); // Ensure that the effect runs only when the handler function changes

    return (
        <main>
            <img src={RestaurantTable} className='diagonal-cut' alt='a restaurant table'/>
            <Hero />
            <Card className='foodCard'>
                {foodCtx.Food.map((item) => 
                    <FoodItem 
                    name = {item.name}
                    desc = {item.desc}
                    price = {item.price}
                    key = {item.name}
                />
    )}
            </Card>
            {(foodCtx.FoodCounter > 0 && cartDisplay) ? 
                <Cart cartDisplay = {setCartDisplay}/> : null
        }
        </main>
    )
}

export default Main;
