import { useContext, useEffect, useState } from "react";
import ProjectData from "../store/data";
import Button from "../UI/Button";
import "./Header.css"

let counter = 0;

function Header () {
    const dataCtx = useContext(ProjectData);

    let [amountOfItems, setAmountOfItems] = useState(null);
    
    useEffect(() => {
        for (const item of dataCtx.Food) {
            counter += item.amount;
        }
        setAmountOfItems(counter);
    }, [dataCtx.Food])

    return (
        <header>
            <h1>ReactMeals</h1>
            <Button className='cartButton'><i className="fa fa-shopping-cart" style={{'fontSize':'1.25rem'}}></i> 
            <p className="CartTXT">Your Cart</p>
            <span className="numberOfCartItems">{amountOfItems}</span>
            </Button>
        </header>
    )
}

export default Header;
