import { useContext } from "react";
import ProjectData from "../store/data";
import Button from "./Button";
import "./Header.css"

function Header () {
    const dataCtx = useContext(ProjectData);

    return (
        <header>
            <h1>ReactMeals</h1>
            <Button className='cartButton'><i className="fa fa-shopping-cart" style={{'font-size':'1rem'}}></i> <p>Your Cart</p><span className="numberOfCartItems"> {dataCtx.Cart.length}</span></Button>
        </header>
    )
}

export default Header;
