import { useContext } from "react";
import ProjectData from "../store/data";
import Button from "../UI/Button";
import "./Header.css"

function Header(props) {
    const dataCtx = useContext(ProjectData);
  
    console.log(props);
  
    return (
      <header>
        <h1>ReactMeals</h1>
        <Button className='cartButton' onClick={() => props.cartDisplay(true)}>
          <i className="fa fa-shopping-cart" style={{ 'fontSize': '1.25rem' }} ></i>
          <p className="CartTXT">Your Cart</p>
          <span className="numberOfCartItems">{dataCtx.FoodCounter}</span>
        </Button>
      </header>
    )
  }
  

export default Header;
