import React, {useState, useEffect} from "react";
import Main from "./components/Main";
import Header from "./components/Header";

function App() {
  const [cartDisplayHelper, setCartDisplayHelper] = useState(false);

  // Move the state update logic inside a useEffect hook
  useEffect(() => {
    // Pass the state updater function to the Main component
    // This ensures that the Main component doesn't trigger state updates during rendering
    // Instead, it updates the state after the render phase is complete
    return () => setCartDisplayHelper(func => func);
  }, []);

  return (
    <React.Fragment>
      {/* Pass the state updater function down to the Main component */}
      <Main handler={setCartDisplayHelper} />
      <Header cartDisplay={cartDisplayHelper} />
    </React.Fragment>
  );
}

export default App;
