import Card from "../../UI/Card";
import React, {useContext, useEffect} from "react";
import ProjectData from "../../store/data";
import { createPortal, } from "react-dom";
import CartItem from "./CartItem";

const root = document.getElementById('root');

const foodData = useContext(ProjectData);

function Cart() {
    let totalAmountOfFood = 0;
    useEffect( () => {
        let totalAmount = foodData.reduce((sum, item) => sum + item.amount, 0)
        totalAmountOfFood = totalAmount;
    }, [foodData.Food])

    return createPortal(
        <Card>
            {foodData.Food.map((item) => {item.amount > 0 ? 
                <CartItem 
                    name = {item.name}
                    desc = {item.desc}
                    price = {item.price}
                    amount = {item.amount}
                    key = {item.name}
                /> : null
            })}
            <h2>Total amount</h2>
            <p>{totalAmountOfFood}</p>    
        </Card>,
        root
    );
}

export default Cart;
